import React, { Component } from 'react';
// import styled from 'styled-components';
import './Quotes.css';

class Quotes extends Component {
    state = { hover: false };
    listRef = React.createRef();

    handleHover = () => {
        this.setState({ hover: !true });
    };

    randColor = array => {
        var copy = array.slice(0);
        return function() {
            if (copy.length < 1) {
                copy = array.slice(0);
            }
            var index = Math.floor(Math.random() * copy.length);
            var item = copy[index];
            copy.splice(index, 1);
            return item;
        };
    };

    render() {
        const hoverColor = 'transparent';
        const backColor = this.randColor(this.props.color);
        if (this.props.gameWon) {
            return (
                <div className='QuoteContainer'>
                    <ul className='AllQuotes'>
                        {this.props.quotes.map(quote => (
                            <li
                                key={quote.id}
                                className='SingleQuote'
                                style={{
                                    backgroundColor: this.listRef.current.style
                                        .backgroundColor
                                }}
                            >
                                <h3 className='quote'>
                                    {this.props.author.quote}
                                </h3>
                                <div className='quote-about'>
                                    <span className='quote-season'>
                                        Season: {this.props.author.season}
                                    </span>
                                    <span className='quote-episode'>
                                        Episode: {this.props.author.episode}
                                    </span>
                                </div>
                            </li>
                        ))}
                    </ul>
                </div>
            );
        } else if (this.props.gameWon === false) {
            return (
                <div className='QuoteContainer'>
                    <ul className='AllQuotes'>
                        {this.props.quotes.map(quote => (
                            <li
                                key={quote.id}
                                className='SingleQuote'
                                style={{
                                    backgroundColor: this.state.hover
                                        ? hoverColor
                                        : backColor
                                }}
                                onClick={() =>
                                    this.props.handleChoice(quote.id)
                                }
                                ref={this.listRef}
                                onMouseOver={this.handleHover}
                            >
                                <h3 className='quote'>{quote.quote}</h3>
                                <div className='quote-about'>
                                    <span className='quote-season'>
                                        Season: {quote.season}
                                    </span>
                                    <span className='quote-episode'>
                                        Episode: {quote.episode}
                                    </span>
                                </div>
                            </li>
                        ))}
                    </ul>
                </div>
            );
        }
    }
}

export default Quotes;
